import { useState } from "react"
import { createElement } from "react"

function Square({value, onSquareClick}){
    return (
            <button className="square" onClick={onSquareClick}>{value}</button>
    )
}
function Board({xIsNext, squares, onPlay}){

    const winner = determineWinner(squares)

    const nextPlayer = winner ? `Winner: ${winner}` : xIsNext ? 'Next player: X' : 'Next player: O' 


    function CreateSquares({iter}){        
        const divSquares = []
        for(let i = 0; i < 3; i++){
            divSquares.push(<Square value={squares[iter * 3 + i]} onSquareClick={()=>handleClick(iter * 3 + i)}/>)
        }
        return divSquares
    }

    function GameDivs(){
        const gSquares = []
        for(let j = 0; j < 3; j++){
            gSquares.push(
                <div className="board-row">
                    <CreateSquares iter={j}/>
                </div>
            )
        }
        return (gSquares)
    }

    function GameDivsCreateElement(){
        let i = 0
        const divs = []
        while(i < 3){
            const innerDivs = []
            for(let j = 0; j < 3; j ++){
                innerDivs.push(
                    <Square value={squares[i * 3 + j]} onSquareClick={()=>handleClick(i * 3 + j)}/> 
                )

            }
            divs.push(
                <div className="board-row">
                    {innerDivs}
                </div>
            )
            i++
        }
        return divs
    }

    return (
        <>
        <div className="turn">{nextPlayer}</div>
          <GameDivsCreateElement/>
        </>
    )

    function handleClick(i){
        if(squares[i] || determineWinner(squares)){
            return
        }
        const nextSquares = squares.slice()
        if(xIsNext){
            nextSquares[i] = 'X'
        }
        else{
            nextSquares[i] = 'O'
        }
        onPlay(nextSquares)
    }

    function determineWinner(squares){
        const lines = [
            [0,1,2],
            [3,4,5],
            [6,7,8],
            [0,4,8],
            [2,4,6],
            [0,3,6],
            [1,4,7],
            [2,5,8]
        ]
        for(let i in lines){
            const [a,b,c] = lines[i]
            if(squares[a] && squares[a] === squares[b] && squares[a] === squares[c]){
                return squares[a]//return the sign of the winner
            }
        }
        return null
    }
}

export default function Game(){
    const [history, setHistory] = useState([Array(9).fill(null)])
    const [xIsNext, setXisNext] = useState(true)
    const [currentMove, setCurrentMove] = useState(0)
    const currentSquares = history[currentMove]

    const moves = history.map((squares,move)=>{
        let description = ''

        if(move > 0){
            description = `Go to move ${move}`
        }else if(move === currentMove){
            description = `You are at move ${move}`
        }
        else{
            description = 'Go to game start'
        }

        const ListItem = ()=>{ 
                        if(move === currentMove){
                           return createElement('p',null,`You are at move ${currentMove}`)
                        }
                        else{
                           return createElement('button',{onClick:()=>jumpTo(move)},description)
                        }
                    }

        return(
            <li key={move}>
                <ListItem/>
            </li>
        )
    })

    return(
        <div className="game">
            <div className="game-board">
                <Board xIsNext={xIsNext} squares={currentSquares} onPlay={handlePlay}/>
            </div>
            <div className="game-info">
                <ol>{moves}</ol>
            </div>
            <button /**onClick={newGame} */>New game</button>
        </div>
    )

    function handlePlay(nextSquares){
        const nextHistory = [...history.slice(0,currentMove + 1),nextSquares]
        setHistory(nextHistory)
        setCurrentMove(nextHistory.length - 1)
        setXisNext(!xIsNext)    
    }

    function jumpTo(nextMove){
        setCurrentMove(nextMove)
        setXisNext(nextMove % 2 === 0)
    }
}
